  let [name, setName] = useState(['Ïª§Ìîº','ÏπòÏ¶à','Ìå¨Ìã∞']);

  function nameAdd(){
    let copy = name.slice();
    copy.push(input)
    setName(copy)
  }
  let [input,setInput] = useState('');



  return (
    <div className="App">
      <div className='black-nav'>
        <div>Í∞úÎ∞ú blog</div>
      </div>
      <h1 style={ { color : 'blue', fontSize:'30px' }}>{ posts }</h1>
