      <button onClick={ onChange }>Î≥ÄÍ≤Ω</button>
      <button onClick={ checkFunc }>ÎàåÎü¨Î¥ê</button>

      {
        check === true
        ? <Modal title={value}></Modal>
        : null
      }

      {
        name.map(function(n){
          return(
          <div className='list' >
            <h2> {n} </h2>
            <hr></hr>
          </div>
