import logo from './logo.svg';
import './App.css';
import { useState } from 'react';
import Modal from './pages/Modal';
function App() {
  let posts = '강남 고기'

  let [msg,setMsg] = useState(['남자 코트 추천','강남 우동 맛집','파이썬 독학']);
  let [value,setValue] = useState(msg[0])

  let [title,setTitle] = useState('글제목');
  function onChange(){
    console.log(msg)
    let copy = msg.slice();
    copy[0] = '여자코트 추천'
    console.log("네?")
    console.log(copy)
    setMsg()
    setMsg(copy)

  }

  let [check,setCheck] = useState(false);

  function checkFunc(){
    if(check === true){
      setCheck(false);
    }
    else{
      setCheck(true);
    }
  }

  let [num,setNum] = useState(0);
  let [name, setName] = useState(['커피','치즈','팬티']);
  let data = ['김치','치즈','스마일'];
  let name_copy = data.slice().map(function(n){
    console.log(n);
    return(
    <div className='list'>
      <h2> {n} </h2>
      <hr></hr>
    </div>
    )
  })
  console.log(name_copy)


  return (
    <div className="App">
      <div className='black-nav'>
        <div>개발 blog</div>
      </div>
      <h1 style={ { color : 'blue', fontSize:'30px' }}>{ posts }</h1>
      <div className='list' onClick={()=>{setValue(msg[0])}}>
        <h4>{ msg[0] }<span onClick={ ()=>{
          setNum(num+1)
          console.log({num})} }>
            🍠</span>{num}</h4>
        <p>날짜</p>
        <hr/>
      </div>
      <div className='list' onClick={()=>{setValue(msg[1])}}>
        <h4>{ msg[1] }</h4>
        <p>날짜</p>
        <hr/>
      </div>
      <div className='list' onClick={()=>{setValue(msg[2])}}>
        <h4>{ msg[2] }</h4>
        <p>날짜</p>
        <hr/>
      </div>
      <button onClick={ onChange }>변경</button>
      <button onClick={ checkFunc }>눌러봐</button>

      {
        check === true
        ? <Modal title={value}></Modal>
        : null
      }

      {
        name.map(function(n){
          return(
          <div className='list' >
            <h2> {n} </h2>
            <hr></hr>
          </div>
          )
        })
      }

      <div>
        <input onChange={(e)=>{console.log(e.target.value)}}></input>
      </div>

    </div>


  );

  // function Modal(props){
  //   return(
  //     <div className='modal'>
  //       <h2>{props.msgv[0]}</h2>
  //       <p>상세 내용</p>
  //     </div>
  //   )
  // }
  }

export default App;
